        family: 'Open Sans'
      }
    },
    secondary: {
      color: 'rgb(102, 102, 102)'
    },
    error: 'rgb(255, 0, 0)',
    valid: 'rgb(0, 128, 0)'
  },
  buttons: {
    primary: {
      font: {
        family: 'Roboto'
      },
      color: 'white',
      background: 'rgb(0, 128, 255)',
      borderRadius: 4,
      hover: {
        background: 'rgba(0, 128, 255, 0.8)'
      }
    },
    secondary: {
