import { useAccount } from 'alephium-web3'

function MyComponent() {
  const { account, isConnected } = useAccount()

  return (
    <div>
      {isConnected ? (
        <p>Connected to account {account}</p>
      ) : (
        <p>Not connected to any account</p>
      )}
    </div>
  )
}
```

